mapper.select:
SELECT 

FROM collect a
WHERE a.id = #{id}

        SELECT 

        FROM collect a

			a.del_flag = #{DEL_FLAG_NORMAL}

				AND a.adage_id = #{adage.id}


				AND a.user_id = #{user.id}


				and a.create_date >= #{createDateStart}

mapper.update:
UPDATE collect SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
