mapper.select:
SELECT 

FROM sys_user a
WHERE a.id = #{id}

SELECT 

FROM sys_user a
WHERE a.wx_id = #{wxId}

        SELECT 

        FROM sys_user a

			a.del_flag = #{DEL_FLAG_NORMAL}

				AND a.name = #{name}


				AND a.wx_id = #{wxId}


				AND a.allocation = #{allocation}


				AND a.state = #{state}


		order by a.update_date desc
